import React, {Component} from 'react';


class RegisterComponent extends Component{
    constructor(props) {
        super(props)
        this.state = {
            username: '',
            password: ''
        }
        this.handerChange = this.handerChange.bind(this)
        this.handlerClicked = this.handlerClicked.bind(this)
    }

    handerChange(event){
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    handlerClicked(){
        console.log(this.state.username, this.state.value)
    }

    render() {
        return(
            <>
                <h1>Register</h1>
                Username: <input type="text" value={this.state.username} onChange={this.handlerChange}/>
                Password: <input type="text" value={this.state.password} onChange={this.handerChange}/>
                <button onClick={this.handlerClicked}>Submit</button>
            </>
        )
    }

}

export default RegisterComponent
