import React, {Component} from 'react';
import axios from 'axios';


class LoginComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            username: '',
            password: ''
        }
        this.handlerChange = this.handlerChange.bind(this);
        this.loginClick = this.loginClick.bind(this);
    }

    handlerChange(event) {
        // console.log([event.target.name], event.target.value)
        this.setState(
            {
                [event.target.name]: event.target.value
            }
        )
    }

    loginClick() {
        // console.log(this.state.username, this.state.password)
        let data = {username: this.state.name, password: this.state.password}
        let headers = {Content_Type: "application/json"}
        let json = JSON.stringify(data)
        let res = axios.post('http://localhost:5000/login', {headers: headers},
        json)
        console.log(res.status_code)
    }

    render () {
        return (
            <div>
                <h1>Login</h1>
                Username: <input type="text" name="username" 
                value={this.state.username} onChange={this.handlerChange}/>
                Password: <input type="password" name="password" 
                value={this.state.password} onChange={this.handlerChange}/>
                <button onClick={this.loginClick}>Login</button>
            </div>
        )
    }
}

export default LoginComponent
